<?php/* * recursos.inc.php */$GLOBALS['label'] = array ();loadLabelDB();//===============================================================function loadLabelDB () {    $GLOBALS['label'] = array (        'key' => 'text'    );        }//function getLabel ($id) {    if (isset ($GLOBALS['label'][$id]))        return $GLOBALS['label'][$id];    else        return '##'.$id.'##';}function printLabel ($id) {    print getLabel($id);}function labelList () {    return array_keys ($GLOBALS['label']);}function replaceLabel ($txt) {    foreach (labelList() as $k)        $txt = str_replace ("##$k##",getLabel($k),$txt);    return $txt;}